import React, { useState, useEffect, useRef } from 'react';
import Papa from 'papaparse';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import './App.css';

function App() {
  // Reference data state
  const [referenceData, setReferenceData] = useState([]);
  const [indexedData, setIndexedData] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [referenceLoaded, setReferenceLoaded] = useState(false);
  const [referenceCSV, setReferenceCSV] = useState('');

  // Input state
  const [userCSV, setUserCSV] = useState('');
  const [csvData, setCsvData] = useState([]);
  const [parseProgress, setParseProgress] = useState(0);
  const [batchSize, setBatchSize] = useState(500);
  const [currentBatch, setCurrentBatch] = useState(1);
  const [processingBatch, setProcessingBatch] = useState(false);

  // Results state
  const [matchedResults, setMatchedResults] = useState([]);
  const [matchStats, setMatchStats] = useState({
    total: 0,
    matched: 0,
    unmatched: 0,
    byPOS: {}
  });
  const [showUnmatched, setShowUnmatched] = useState(false);

  // Refs for scrolling
  const resultsRef = useRef(null);

  // Load the reference data from textarea
  const loadReferenceData = () => {
    if (!referenceCSV.trim()) {
      setError('Please paste your reference lemma data first.');
      return;
    }

    try {
      setLoading(true);
      setError('');

      Papa.parse(referenceCSV, {
        header: true,
        skipEmptyLines: true,
        dynamicTyping: true,
        complete: (result) => {
          // Check if the CSV has the required columns
          if (result.data.length > 0) {
            const firstRow = result.data[0];

            // Check if the lemma data has the necessary columns
            if (!firstRow['Words (Frequency)'] && !firstRow['Lemma']) {
              setError('Reference CSV must have "Words (Frequency)" and "Lemma" columns. Please check your format.');
              setLoading(false);
              return;
            }

            setReferenceData(result.data);

            // Create an efficient index for lookups - ONLY exact matches
            const wordIndex = {};

            result.data.forEach((row, rowIdx) => {
              if (row['Words (Frequency)']) {
                const wordString = row['Words (Frequency)'].toLowerCase();

                // Extract individual words and their frequencies
                const wordEntries = wordString.split(',');

                for (const entry of wordEntries) {
                  const trimmed = entry.trim();
                  // Try to match the pattern "word (count)"
                  const match = trimmed.match(/^([^\(]+)(?:\s*\(\d+\))?/);

                  if (match) {
                    const word = match[1].trim().toLowerCase();

                    // Exact match index only
                    if (!wordIndex[word]) {
                      wordIndex[word] = [];
                    }
                    wordIndex[word].push(rowIdx);
                  }
                }
              }
            });

            // Only store exact match index - remove substring index
            setIndexedData({
              exact: wordIndex
            });

            setReferenceLoaded(true);
            setLoading(false);
          } else {
            setError('Reference CSV appears to be empty. Please check your data.');
            setLoading(false);
          }
        },
        error: (error) => {
          setError('Error parsing reference CSV: ' + error.message);
          setLoading(false);
        }
      });
    } catch (err) {
      setError('Failed to process reference CSV: ' + err.message);
      setLoading(false);
    }
  };

  // Parse the user-provided CSV data
  const parseUserCSV = () => {
    if (!userCSV.trim()) return;

    try {
      Papa.parse(userCSV, {
        header: true,
        skipEmptyLines: true,
        dynamicTyping: true,
        complete: (result) => {
          // Check if the CSV has the required columns
          if (result.data.length > 0) {
            const firstRow = result.data[0];
            if (!('Word' in firstRow) && !('word' in firstRow)) {
              setError('CSV must have a "Word" column. Please check your CSV format.');
              return;
            }

            // Normalize column names (handle case variations)
            const normalizedData = result.data.map(row => {
              const normalizedRow = {};

              // Handle different column case names
              Object.keys(row).forEach(key => {
                const lowerKey = key.toLowerCase();
                if (lowerKey === 'word') {
                  normalizedRow.Word = row[key];
                } else if (lowerKey === 'freq' || lowerKey === 'frequency') {
                  normalizedRow.Freq = row[key];
                } else {
                  normalizedRow[key] = row[key];
                }
              });

              return normalizedRow;
            });

            setCsvData(normalizedData);
            setCurrentBatch(1);
            setMatchedResults([]);
            setMatchStats({
              total: normalizedData.length,
              matched: 0,
              unmatched: 0,
              byPOS: {}
            });
            setParseProgress(0);
          } else {
            setError('CSV appears to be empty. Please check your data.');
          }
        },
        error: (error) => {
          setError('Error parsing your CSV: ' + error.message);
        }
      });
    } catch (err) {
      setError('Failed to process CSV: ' + err.message);
    }
  };

  // Find matches for a word - MODIFIED to only return exact matches
  const findMatches = (word) => {
    if (!word || typeof word !== 'string') return [];

    const searchWord = word.toLowerCase().trim();

    // Only use exact matches
    if (indexedData.exact && indexedData.exact[searchWord]) {
      return indexedData.exact[searchWord].map(idx => referenceData[idx]);
    }

    // No substring matching - return empty array for non-exact matches
    return [];
  };

  // Process the current batch
  const processBatch = () => {
    if (csvData.length === 0) return;

    setProcessingBatch(true);

    // Calculate batch indices
    const startIdx = (currentBatch - 1) * batchSize;
    const endIdx = Math.min(startIdx + batchSize, csvData.length);

    // Get the current batch
    const currentItems = csvData.slice(startIdx, endIdx);

    // Use a timeout to give the UI a chance to update
    setTimeout(() => {
      // Process each word in the batch
      const batchResults = [];
      const newMatchStats = {...matchStats};

      currentItems.forEach((item, idx) => {
        const word = item.Word || '';
        const frequency = item.Freq || 0;

        // Find matches for this word
        const matches = findMatches(word);

        // Update statistics
        if (matches.length > 0) {
          newMatchStats.matched += 1;

          // Update POS statistics
          matches.forEach(match => {
            const pos = match.POS || 'Unknown';
            newMatchStats.byPOS[pos] = (newMatchStats.byPOS[pos] || 0) + 1;
          });
        } else {
          newMatchStats.unmatched += 1;
        }

        // Add to results
        batchResults.push({
          word,
          frequency,
          matches,
          originalIndex: startIdx + idx
        });

        // Update progress
        setParseProgress(Math.round(((startIdx + idx + 1) / csvData.length) * 100));
      });

      // Update state with new results
      setMatchedResults(prev => [...prev, ...batchResults]);
      setMatchStats(newMatchStats);
      setProcessingBatch(false);

      // Scroll to results
      if (resultsRef.current) {
        resultsRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    }, 100);
  };

  // Process next batch
  const processNextBatch = () => {
    if (currentBatch * batchSize < csvData.length) {
      setCurrentBatch(prev => prev + 1);
    }
  };

  // Reset all results
  const resetResults = () => {
    setCsvData([]);
    setMatchedResults([]);
    setParseProgress(0);
    setCurrentBatch(1);
    setMatchStats({
      total: 0,
      matched: 0,
      unmatched: 0,
      byPOS: {}
    });
  };

  // Copy results to clipboard
  const copyResultsToClipboard = () => {
    if (matchedResults.length === 0) return;

    try {
      // Generate the data to copy
      const data = generateExportData();

      // Copy to clipboard
      navigator.clipboard.writeText(data)
        .then(() => {
          // Show success message
          setError('Results copied to clipboard successfully!');
          setTimeout(() => setError(''), 3000);
        })
        .catch(err => {
          // Fallback method for older browsers
          const textArea = document.createElement('textarea');
          textArea.value = data;
          textArea.style.position = 'fixed';  // Avoid scrolling to bottom
          document.body.appendChild(textArea);
          textArea.focus();
          textArea.select();

          try {
            const successful = document.execCommand('copy');
            if (successful) {
              setError('Results copied to clipboard successfully!');
            } else {
              setError('Unable to copy results. You may need to copy manually from the table.');
            }
            setTimeout(() => setError(''), 3000);
          } catch (err) {
            setError('Unable to copy results. You may need to copy manually from the table.');
            setTimeout(() => setError(''), 3000);
          }

          document.body.removeChild(textArea);
        });
    } catch (err) {
      setError('Failed to copy: ' + err.message);
      setTimeout(() => setError(''), 3000);
    }
  };

  // Generate export data in TSV format
  const generateExportData = () => {
    const headers = "Word\tFrequency\tLemma\tPOS\tGloss\n";

    const rows = matchedResults.flatMap(result => {
      if (result.matches.length === 0) {
        // If showing unmatched words, include them with empty lemma/POS/gloss
        return showUnmatched ? [`${result.word}\t${result.frequency}\t\t\t`] : [];
      }

      // Generate a row for each match
      return result.matches.map(match =>
        `${result.word}\t${result.frequency}\t${match.Lemma || ''}\t${match.POS || ''}\t${match.Gloss || ''}`
      );
    }).join('\n');

    return headers + rows;
  };

  // Trigger batch processing when currentBatch changes
  useEffect(() => {
    if (csvData.length > 0 && !processingBatch) {
      processBatch();
    }
  }, [currentBatch, csvData]);

  // Calculate if we have more batches to process
  const hasMoreBatches = csvData.length > 0 && currentBatch * batchSize < csvData.length;

  // Calculate how many words we've processed so far
  const processedCount = Math.min(currentBatch * batchSize, csvData.length);

  // Prepare data for the POS distribution chart
  const chartData = Object.entries(matchStats.byPOS)
    .map(([pos, count]) => ({
      pos,
      count,
      percentage: Math.round((count / matchStats.matched) * 100)
    }))
    .sort((a, b) => b.count - a.count);

  return (
    <div className="p-4 max-w-7xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">CSV Batch Lemma Matcher (Exact Match Only)</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
          <button
            className="float-right font-bold"
            onClick={() => setError('')}
          >
            ×
          </button>
        </div>
      )}

      {loading ? (
        <div className="text-center py-8">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mb-2"></div>
          <p>Processing data...</p>
        </div>
      ) : (
        <div className="space-y-6">
          {/* Step 1: Load reference data */}
          <div className="bg-white p-4 rounded shadow">
            <h2 className="text-xl font-semibold mb-3">Step 1: Load Reference Lemma Data</h2>

            <div className="mb-3">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Paste your lemmatized reference data (must include "Words (Frequency)", "Lemma", "POS", and "Gloss" columns):
              </label>
              <textarea
                className="w-full p-2 border rounded h-32 font-mono text-sm"
                value={referenceCSV}
                onChange={(e) => setReferenceCSV(e.target.value)}
                placeholder={`Words (Frequency),Lemma,Total,POS,Gloss
aris (3071), ariso (20), ari (19),არის,6907,VERB,is
...`}
                disabled={referenceLoaded}
              />
            </div>

            <div className="flex gap-3 items-center">
              {!referenceLoaded ? (
                <button
                  className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
                  onClick={loadReferenceData}
                  disabled={!referenceCSV.trim() || loading}
                >
                  Load Reference Data
                </button>
              ) : (
                <div className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm">
                  ✓ Reference data loaded ({referenceData.length} entries)
                </div>
              )}

              {referenceLoaded && (
                <button
                  className="text-blue-600 hover:text-blue-800 text-sm"
                  onClick={() => {
                    setReferenceLoaded(false);
                    setReferenceData([]);
                    setIndexedData({});
                  }}
                >
                  Reset Reference Data
                </button>
              )}
            </div>
          </div>

          {/* Step 2: Input word list */}
          <div className={`bg-white p-4 rounded shadow ${!referenceLoaded ? 'opacity-50' : ''}`}>
            <h2 className="text-xl font-semibold mb-3">Step 2: Input Your Word List</h2>

            <div className="mb-3">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Paste your CSV data (must include "Word" and optionally "Freq" columns):
              </label>
              <textarea
                className="w-full p-2 border rounded h-48 font-mono text-sm"
                value={userCSV}
                onChange={(e) => setUserCSV(e.target.value)}
                placeholder="Freq,Word
4943,da
3753,es
3510,ra
..."
                disabled={!referenceLoaded}
              />
            </div>

            <div className="flex flex-wrap gap-3 items-center">
              <button
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
                onClick={parseUserCSV}
                disabled={!referenceLoaded || !userCSV.trim() || processingBatch}
              >
                Parse CSV
              </button>

              <div className="flex items-center">
                <label className="text-sm font-medium text-gray-700 mr-2">
                  Batch Size:
                </label>
                <select
                  className="border rounded p-1"
                  value={batchSize}
                  onChange={(e) => setBatchSize(Number(e.target.value))}
                  disabled={!referenceLoaded}
                >
                  <option value={100}>100 words</option>
                  <option value={250}>250 words</option>
                  <option value={500}>500 words</option>
                  <option value={1000}>1000 words</option>
                  <option value={2000}>2000 words</option>
                </select>
              </div>

              {csvData.length > 0 && (
                <span className="text-sm text-gray-600">
                  {processedCount} of {csvData.length} words processed ({Math.round((processedCount / csvData.length) * 100)}%)
                </span>
              )}

              {csvData.length > 0 && (
                <button
                  className="text-red-600 hover:text-red-800 text-sm"
                  onClick={resetResults}
                >
                  Reset
                </button>
              )}
            </div>

            {parseProgress > 0 && parseProgress < 100 && (
              <div className="w-full bg-gray-200 rounded-full h-2.5 mt-4">
                <div
                  className="bg-blue-600 h-2.5 rounded-full"
                  style={{ width: `${parseProgress}%` }}
                ></div>
              </div>
            )}
          </div>

          {/* Step 3: Results */}
          {matchedResults.length > 0 && (
            <div className="bg-white p-4 rounded shadow" ref={resultsRef}>
              <h2 className="text-xl font-semibold mb-3">Step 3: Matching Results</h2>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                <div className="bg-gray-50 p-3 rounded">
                  <h3 className="font-semibold mb-2">Summary</h3>
                  <ul className="space-y-1">
                    <li>Total Words: <span className="font-medium">{matchStats.total}</span></li>
                    <li>Words Processed: <span className="font-medium">{processedCount}</span></li>
                    <li>Matched Words: <span className="font-medium text-green-600">{matchStats.matched}</span> ({Math.round((matchStats.matched / processedCount) * 100)}%)</li>
                    <li>Unmatched Words: <span className="font-medium text-red-600">{matchStats.unmatched}</span> ({Math.round((matchStats.unmatched / processedCount) * 100)}%)</li>
                  </ul>
                </div>

                {chartData.length > 0 && (
                  <div className="bg-gray-50 p-3 rounded h-64">
                    <h3 className="font-semibold mb-2">POS Distribution</h3>
                    <ResponsiveContainer width="100%" height="90%">
                      <BarChart
                        data={chartData.slice(0, 10)} // Show top 10 POS categories
                        margin={{ top: 5, right: 20, left: 10, bottom: 5 }}
                      >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="pos" />
                        <YAxis />
                        <Tooltip formatter={(value, name, props) => [`${value} (${props.payload.percentage}%)`, 'Count']} />
                        <Bar dataKey="count" fill="#3b82f6" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                )}
              </div>

              <div className="flex flex-wrap gap-3 mb-4">
                {hasMoreBatches && (
                  <button
                    className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded"
                    onClick={processNextBatch}
                    disabled={processingBatch}
                  >
                    {processingBatch ? (
                      <span>Processing...</span>
                    ) : (
                      <span>Process Next Batch ({Math.min(batchSize, csvData.length - processedCount)} words)</span>
                    )}
                  </button>
                )}

                <button
                  className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
                  onClick={copyResultsToClipboard}
                  disabled={matchedResults.length === 0}
                >
                  Copy Results to Clipboard
                </button>

                <div className="flex items-center ml-auto">
                  <input
                    type="checkbox"
                    id="showUnmatched"
                    checked={showUnmatched}
                    onChange={(e) => setShowUnmatched(e.target.checked)}
                    className="mr-2"
                  />
                  <label htmlFor="showUnmatched" className="text-sm">
                    Show unmatched words
                  </label>
                </div>
              </div>

              <div className="overflow-x-auto">
                <table className="min-w-full border-collapse border border-gray-300">
                  <thead>
                    <tr className="bg-gray-100">
                      <th className="border border-gray-300 px-4 py-2 text-left">Word</th>
                      <th className="border border-gray-300 px-4 py-2 text-left">Frequency</th>
                      <th className="border border-gray-300 px-4 py-2 text-left">Lemma</th>
                      <th className="border border-gray-300 px-4 py-2 text-left">POS</th>
                      <th className="border border-gray-300 px-4 py-2 text-left">Gloss</th>
                    </tr>
                  </thead>
                  <tbody>
                    {matchedResults.map((result) => {
                      if (result.matches.length === 0) {
                        // If no matches and we're not showing unmatched, skip
                        if (!showUnmatched) return null;

                        // Show unmatched word with empty cells
                        return (
                          <tr key={`unmatched-${result.originalIndex}`} className="bg-red-50">
                            <td className="border border-gray-300 px-4 py-2">{result.word}</td>
                            <td className="border border-gray-300 px-4 py-2">{result.frequency}</td>
                            <td className="border border-gray-300 px-4 py-2 text-red-500">Not found</td>
                            <td className="border border-gray-300 px-4 py-2">-</td>
                            <td className="border border-gray-300 px-4 py-2">-</td>
                          </tr>
                        );
                      }

                      // For matched words, show a row for each match
                      return result.matches.map((match, matchIdx) => (
                        <tr
                          key={`match-${result.originalIndex}-${matchIdx}`}
                          className={matchIdx > 0 ? "bg-blue-50" : ""}
                        >
                          <td className="border border-gray-300 px-4 py-2">
                            {matchIdx === 0 ? result.word : ""}
                          </td>
                          <td className="border border-gray-300 px-4 py-2">
                            {matchIdx === 0 ? result.frequency : ""}
                          </td>
                          <td className="border border-gray-300 px-4 py-2">{match.Lemma}</td>
                          <td className="border border-gray-300 px-4 py-2">{match.POS}</td>
                          <td className="border border-gray-300 px-4 py-2">{match.Gloss}</td>
                        </tr>
                      ));
                    })}
                  </tbody>
                </table>
              </div>

              {hasMoreBatches && (
                <div className="mt-4 text-center">
                  <button
                    className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded"
                    onClick={processNextBatch}
                    disabled={processingBatch}
                  >
                    {processingBatch ? (
                      <span>Processing...</span>
                    ) : (
                      <span>Process Next Batch ({Math.min(batchSize, csvData.length - processedCount)} words)</span>
                    )}
                  </button>
                </div>
              )}
            </div>
          )}

          {/* Instructions */}
          <div className="bg-blue-50 p-4 rounded">
            <h3 className="font-semibold mb-2">Instructions</h3>
            <ol className="list-decimal pl-5 space-y-1">
              <li><strong>Step 1:</strong> Paste your lemmatized reference data (the data with Words, Lemmas, POS, etc.) and click "Load Reference Data"</li>
              <li><strong>Step 2:</strong> Paste your word list CSV (with Word and Freq columns) and click "Parse CSV"</li>
              <li><strong>Step 3:</strong> The tool will process words in batches for better performance</li>
              <li>After each batch is processed, review the matches and click "Process Next Batch" to continue</li>
              <li>When finished, use the "Copy Results" button to copy all matches to your clipboard</li>
            </ol>
            <p className="mt-2 text-sm text-gray-600">
              Note: This tool uses <strong>exact matching only</strong>. Any word that doesn't have an exact match in the reference data will be marked as "Not found".
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
