import React, { useState, useEffect, useRef } from 'react';
import Papa from 'papaparse';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

function App() {
  // Reference data state
  const [referenceData, setReferenceData] = useState([]);
  const [indexedData, setIndexedData] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [referenceLoaded, setReferenceLoaded] = useState(false);
  const [referenceCSV, setReferenceCSV] = useState('');

  // Georgian to Latin transcription mapping
  const georgianToLatin = {
    'ა': 'a', 'ბ': 'b', 'გ': 'g', 'დ': 'd', 'ე': 'e', 'ვ': 'v', 'ზ': 'z', 'თ': 't',
    'ი': 'i', 'კ': 'k', 'ლ': 'l', 'მ': 'm', 'ნ': 'n', 'ო': 'o', 'პ': 'p', 'ჟ': 'zh',
    'რ': 'r', 'ს': 's', 'ტ': 't', 'უ': 'u', 'ფ': 'f', 'ქ': 'k', 'ღ': 'gh', 'ყ': 'q',
    'შ': 'sh', 'ჩ': 'ch', 'ც': 'c', 'ძ': 'dz', 'წ': 'c', 'ჭ': 'ch', 'ხ': 'x', 'ჯ': 'j', 'ჰ': 'h'
  };

  // Helper function to count morphemes
  const countMorphemes = (morphemes) => {
    if (!morphemes || typeof morphemes !== 'string' || morphemes.trim() === '') return 0;
    return morphemes.split('-').filter(part => part.trim().length > 0).length;
  };

  // REFINED Georgian morphological analyzer based on the provided lexicon
  const georgianMorphologyAnalyzer = {
    preverbs: ['გა', 'მო', 'და', 'ა', 'შე', 'წა', 'მი', 'გადა', 'გამო', 'შემო', 'ჩა', 'ამო', 'წამო', 'გან'],
    personMarkers: {
      'ვ': '1SUBJ', 'მ': '1OBJ', 'გ': '2OBJ', 'ჰ': '3OBJ', 'ს': '3OBJ/3SUBJ', 'ხ': '2SUBJ', 'გვ': '1plOBJ',
      'თ': '2plSUBJ/PL', 'ან': '3plSUBJ', 'ენ': '3plSUBJ', 'ეს': '3plSUBJ', 'ნენ': '3plSUBJ'
    },
    versionVowels: {
      'ა': 'VER.VOW', 'ი': 'VER.VOW', 'უ': 'VER.VOW', 'ე': 'VER.VOW'
    },
    tenseAspectMood: {
      'ებ': 'TAM (Present/Future Stem)', 'ობ': 'TAM (Present/Future Stem)', 'ავ': 'TAM (Present/Future Stem)', 'ამ': 'TAM (Present/Future Stem)', 'ი': 'TAM (Aorist/Perfect)',
      'ა': 'TAM (Aorist/Perfect)', 'ე': 'TAM (Aorist/Imperative)', 'ო': 'TAM (Optative)', 'ს': 'TAM (3sg Present)',
      'დ': 'TAM (Imperfect/Passive)', 'ილ': 'TAM (Perfect Participle)', 'ულ': 'TAM (Past Participle)'
    },
    nounAffixes: {
      'ი': 'CASE (Nominative)', 'მა': 'CASE (Ergative)', 'მ': 'CASE (Ergative)', 'ს': 'CASE (Genitive/Dative)', 'ით': 'CASE (Instrumental)',
      'ად': 'CASE (Adverbial)', 'ო': 'CASE (Vocative)', 'ში': 'CASE (Inessive)', 'ზე': 'CASE (Superessive)', 'დან': 'CASE (Ablative)',
      'მდე': 'CASE (Terminative)', 'თან': 'CASE (Comitative)', 'კენ': 'CASE (Adessive)', 'გან': 'CASE (Originative)',
      'ებ': 'PL', 'ნი': 'PL'
    },
    particles: {
      'ც': 'PART (also/too)', 'ო': 'PART (Quotative)', 'მეთქი': 'PART (I said)', 'ვე': 'PART (indeed)', 'ღა': 'PART (else)'
    },
    derivational: {
      'ელ': 'DER (Origin)', 'ურ': 'DER (Adjective)', 'იკო': 'DER (Diminutive)', 'უნა': 'DER (Diminutive)',
      'ულ': 'DER (Adjective)', 'იან': 'DER (Possessive Adj)', 'ობ': 'DER (Abstract Noun)', 'სა': 'DER (Purpose)'
    }
  };

  // REFINED function to analyze morpheme structure
  const analyzeMorphemeStructure = (morphemes, pos) => {
    if (!morphemes || typeof morphemes !== 'string' || morphemes.trim() === '') {
      return { structure: 'No morphemes', analysis: 'No morphological data available', complexity: 0, pattern: 'SIMPLE' };
    }

    const parts = morphemes.split('-').filter(part => part.trim().length > 0);
    const structure = [];
    const analysis = [];
    let rootFound = false;

    parts.forEach((part, index) => {
      const cleanPart = part.trim();
      let identified = false;

      // Check for specific grammatical markers first, in a logical order
      if (georgianMorphologyAnalyzer.preverbs.includes(cleanPart) && index < 2) {
        structure.push('PREV');
        analysis.push(`${cleanPart}(preverb)`);
        identified = true;
      }

      if (!identified && georgianMorphologyAnalyzer.personMarkers[cleanPart]) {
        const role = georgianMorphologyAnalyzer.personMarkers[cleanPart];
        structure.push(`PERS.${role.includes('SUBJ') ? 'S' : 'O'}`);
        analysis.push(`${cleanPart}(${role.toLowerCase()})`);
        identified = true;
      }

      if (!identified && pos === 'VERB' && georgianMorphologyAnalyzer.versionVowels[cleanPart] && index < (parts.length -1) ) {
        structure.push('VER.VOW');
        analysis.push(`${cleanPart}(version vowel)`);
        identified = true;
      }

      if (!identified && georgianMorphologyAnalyzer.tenseAspectMood[cleanPart]) {
          const role = georgianMorphologyAnalyzer.tenseAspectMood[cleanPart];
          // Special handling for '-d-' which can be a passive marker
          if(cleanPart === 'დ') {
            structure.push('PASS');
            analysis.push(`${cleanPart}(passive/medial)`);
          } else {
            structure.push('TAM');
            analysis.push(`${cleanPart}(${role.replace('TAM ', '')})`);
          }
          identified = true;
      }

      if (!identified && (pos === 'NOUN' || pos === 'PROPN' || pos === 'PRON' || pos === 'ADJ' || pos === 'PTCPL') && georgianMorphologyAnalyzer.nounAffixes[cleanPart]) {
          const role = georgianMorphologyAnalyzer.nounAffixes[cleanPart];
          structure.push(role.startsWith('CASE') ? 'CASE' : role);
          analysis.push(`${cleanPart}(${role.replace('CASE ', '').toLowerCase()})`);
          identified = true;
      }

      if (!identified && georgianMorphologyAnalyzer.particles[cleanPart]) {
        structure.push('PART');
        analysis.push(`${cleanPart}(${georgianMorphologyAnalyzer.particles[cleanPart].replace('PART ', '').toLowerCase()})`);
        identified = true;
      }

      if (!identified && georgianMorphologyAnalyzer.derivational[cleanPart]) {
        structure.push('DER');
        analysis.push(`${cleanPart}(${georgianMorphologyAnalyzer.derivational[cleanPart].replace('DER ', '').toLowerCase()})`);
        identified = true;
      }

      // If nothing else, assume it's part of the root/stem
      if (!identified) {
        if (!rootFound) {
          structure.push('ROOT');
          analysis.push(`${cleanPart}(root)`);
          rootFound = true;
        } else {
          structure.push('STEM');
          analysis.push(`${cleanPart}(stem)`);
        }
      }
    });

    return {
      structure: structure.join('-'),
      analysis: analysis.join(' + '),
      complexity: parts.length,
      pattern: structure.join('-')
    };
  };

  // REFINED complexity calculation using an absolute scale
  const calculateComplexityLevel = (morphemeCount) => {
    if (morphemeCount <= 0) return 'No data';
    switch (morphemeCount) {
      case 1: return 'Simple (Uninflected)';
      case 2: return 'Low (Inflected)';
      case 3:
      case 4: return 'Moderate';
      case 5:
      case 6: return 'High';
      default: return 'Very High (Agglutinative)';
    }
  };

  // Generate morphological statistics
  const generateMorphologicalStats = (results) => {
    const allMatches = results.flatMap(result => result.matches);
    const stats = {
      totalWords: allMatches.length,
      byPOS: {},
      patterns: {},
      complexityDistribution: {},
      averageComplexityByPOS: {}
    };

    allMatches.forEach(match => {
      const pos = match.POS || 'Unknown';
      const morphCount = countMorphemes(match.Morphemes);
      const analysis = analyzeMorphemeStructure(match.Morphemes, pos);

      if (!stats.byPOS[pos]) {
        stats.byPOS[pos] = { count: 0, totalComplexity: 0, morphemes: [] };
      }
      stats.byPOS[pos].count++;
      stats.byPOS[pos].totalComplexity += morphCount;
      stats.byPOS[pos].morphemes.push(morphCount);

      if (analysis.pattern !== 'SIMPLE') {
        stats.patterns[analysis.pattern] = (stats.patterns[analysis.pattern] || 0) + 1;
      }

      const complexityLevel = calculateComplexityLevel(morphCount);
      stats.complexityDistribution[complexityLevel] = (stats.complexityDistribution[complexityLevel] || 0) + 1;
    });

    Object.keys(stats.byPOS).forEach(pos => {
      const posData = stats.byPOS[pos];
      stats.averageComplexityByPOS[pos] = (posData.totalComplexity / posData.count).toFixed(2);
    });

    return stats;
  };

  const createTranscriptionVariants = (georgianWord) => {
    if (!georgianWord || typeof georgianWord !== 'string') return [];
    let variants = new Set();
    let basicLatin = '';
    for (let char of georgianWord.toLowerCase()) {
      basicLatin += georgianToLatin[char] || char;
    }
    variants.add(basicLatin);
    variants.add(basicLatin.replace(/k/g, "k'").replace(/t/g, "t'").replace(/p/g, "p'").replace(/q/g, "q'"));
    variants.add(basicLatin.replace(/c/g, 'ts')).add(basicLatin.replace(/c/g, "ts'"));
    variants.add(basicLatin.replace(/ch/g, 'c')).add(basicLatin.replace(/ch/g, "ch'"));
    return Array.from(variants).filter(v => v.length > 0);
  };

  // Input state
  const [userCSV, setUserCSV] = useState('');
  const [csvData, setCsvData] = useState([]);
  const [parseProgress, setParseProgress] = useState(0);
  const [batchSize, setBatchSize] = useState(500);
  const [currentBatch, setCurrentBatch] = useState(1);
  const [processingBatch, setProcessingBatch] = useState(false);

  // Results state
  const [matchedResults, setMatchedResults] = useState([]);
  const [matchStats, setMatchStats] = useState({ total: 0, matched: 0, unmatched: 0, byPOS: {} });
  const [morphologicalStats, setMorphologicalStats] = useState(null);
  const [showUnmatched, setShowUnmatched] = useState(false);
  const [showMorphAnalysis, setShowMorphAnalysis] = useState(true);

  const resultsRef = useRef(null);

  const detectDelimiter = (data) => {
    const firstLine = data.split('\n')[0];
    const tabCount = (firstLine.match(/\t/g) || []).length;
    const commaCount = (firstLine.match(/,/g) || []).length;
    return tabCount > commaCount ? '\t' : ',';
  };

  const loadReferenceData = () => {
    if (!referenceCSV.trim()) {
      setError('Please paste your reference lemma data first.');
      return;
    }
    try {
      setLoading(true);
      setError('');
      const delimiter = detectDelimiter(referenceCSV);
      const fileType = delimiter === '\t' ? 'TSV' : 'CSV';
      Papa.parse(referenceCSV, {
        header: true,
        skipEmptyLines: true,
        dynamicTyping: true,
        delimiter: delimiter,
        complete: (result) => {
          if (result.data.length > 0) {
            const firstRow = result.data[0];
            const headers = Object.keys(firstRow);
            const hasNewFormat = headers.some(h => h.toLowerCase() === 'word') && headers.some(h => h.toLowerCase() === 'morphemes');
            if (!hasNewFormat) {
              setError(`${fileType} must have at least "Word" and "Morphemes" columns.`);
              setLoading(false);
              return;
            }
            setReferenceData(result.data);
            const wordIndex = {};
            result.data.forEach((row, rowIdx) => {
              const wordKey = headers.find(h => h.toLowerCase() === 'word');
              if (wordKey && row[wordKey]) {
                const word = row[wordKey].toString().toLowerCase().trim();
                if (word) {
                  if (!wordIndex[word]) wordIndex[word] = [];
                  wordIndex[word].push(rowIdx);
                }
              }
            });
            setIndexedData({ exact: wordIndex });
            setReferenceLoaded(true);
            setLoading(false);
            setError(`✓ ${fileType} data loaded successfully with ${result.data.length} entries`);
            setTimeout(() => setError(''), 3000);
          } else {
            setError('Reference data appears to be empty.');
            setLoading(false);
          }
        },
        error: (error) => {
          setError('Error parsing reference data: ' + error.message);
          setLoading(false);
        }
      });
    } catch (err) {
      setError('Failed to process reference data: ' + err.message);
      setLoading(false);
    }
  };

  const parseUserCSV = () => {
    if (!userCSV.trim()) return;
    try {
      Papa.parse(userCSV, {
        header: true,
        skipEmptyLines: true,
        dynamicTyping: true,
        complete: (result) => {
          if (result.data.length > 0) {
            const firstRow = result.data[0];
            if (!('Word' in firstRow) && !('word' in firstRow)) {
              setError('CSV must have a "Word" column.');
              return;
            }
            const normalizedData = result.data.map(row => {
              const normalizedRow = {};
              Object.keys(row).forEach(key => {
                const lowerKey = key.toLowerCase();
                if (lowerKey === 'word') normalizedRow.Word = row[key];
                else if (lowerKey === 'freq' || lowerKey === 'frequency') normalizedRow.Freq = row[key];
                else normalizedRow[key] = row[key];
              });
              return normalizedRow;
            });
            setCsvData(normalizedData);
            setCurrentBatch(1);
            setMatchedResults([]);
            setMatchStats({ total: normalizedData.length, matched: 0, unmatched: 0, byPOS: {} });
            setParseProgress(0);
          } else { setError('CSV appears to be empty.'); }
        },
        error: (error) => { setError('Error parsing your CSV: ' + error.message); }
      });
    } catch (err) { setError('Failed to process CSV: ' + err.message); }
  };

  const findMatches = (word) => {
    if (!word || typeof word !== 'string') return [];
    const searchWord = word.toLowerCase().trim();
    let matches = [];
    if (indexedData.exact && indexedData.exact[searchWord]) {
      matches = indexedData.exact[searchWord].map(idx => referenceData[idx]);
    }
    if (matches.length === 0 && /[\u10A0-\u10FF]/.test(searchWord)) {
      const variants = createTranscriptionVariants(searchWord);
      for (const variant of variants) {
        if (indexedData.exact && indexedData.exact[variant]) {
          const variantMatches = indexedData.exact[variant].map(idx => ({ ...referenceData[idx], transcriptionVariant: variant }));
          matches.push(...variantMatches);
        }
      }
    }
    return matches.filter((match, index, self) => index === self.findIndex(m => m.Lemma === match.Lemma && m.Morphemes === match.Morphemes));
  };

  const processBatch = () => {
    if (csvData.length === 0) return;
    setProcessingBatch(true);
    const startIdx = (currentBatch - 1) * batchSize;
    const endIdx = Math.min(startIdx + batchSize, csvData.length);
    const currentItems = csvData.slice(startIdx, endIdx);
    setTimeout(() => {
      const batchResults = [];
      const newMatchStats = { ...matchStats };
      currentItems.forEach((item, idx) => {
        const word = item.Word || '';
        const frequency = item.Freq || 0;
        const matches = findMatches(word);
        if (matches.length > 0) {
          newMatchStats.matched += 1;
          matches.forEach(match => {
            const pos = match.POS || 'Unknown';
            newMatchStats.byPOS[pos] = (newMatchStats.byPOS[pos] || 0) + 1;
          });
        } else { newMatchStats.unmatched += 1; }
        batchResults.push({ word, frequency, matches, originalIndex: startIdx + idx });
        setParseProgress(Math.round(((startIdx + idx + 1) / csvData.length) * 100));
      });
      setMatchedResults(prev => [...prev, ...batchResults]);
      setMatchStats(newMatchStats);
      const allResults = [...matchedResults, ...batchResults];
      setMorphologicalStats(generateMorphologicalStats(allResults));
      setProcessingBatch(false);
      if (resultsRef.current) {
        resultsRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    }, 100);
  };

  const processNextBatch = () => {
    if (currentBatch * batchSize < csvData.length) {
      setCurrentBatch(prev => prev + 1);
    }
  };

  const resetResults = () => {
    setCsvData([]);
    setMatchedResults([]);
    setParseProgress(0);
    setCurrentBatch(1);
    setMatchStats({ total: 0, matched: 0, unmatched: 0, byPOS: {} });
    setMorphologicalStats(null);
  };

  const copyResultsToClipboard = () => {
    if (matchedResults.length === 0) return;
    const headers = showMorphAnalysis ?
      "Word\tFrequency\tLemma\tPOS\tGloss\tMorphemes\tMorpheme_Count\tMorphological_Structure\tMorphological_Analysis\tComplexity_Level\n" :
      "Word\tFrequency\tLemma\tPOS\tGloss\tMorphemes\tMorpheme_Count\n";
    const rows = matchedResults.flatMap(result => {
      if (result.matches.length === 0) {
        return showUnmatched ? (showMorphAnalysis ? [`${result.word}\t${result.frequency}\t\t\t\t\t0\t\t\t`] : [`${result.word}\t${result.frequency}\t\t\t\t\t0`]) : [];
      }
      return result.matches.map(match => {
        const morphCount = countMorphemes(match.Morphemes);
        const morphAnalysis = analyzeMorphemeStructure(match.Morphemes, match.POS);
        const complexityLevel = calculateComplexityLevel(morphCount);
        return showMorphAnalysis ?
          `${result.word}\t${result.frequency}\t${match.Lemma || ''}\t${match.POS || ''}\t${match.Gloss || ''}\t${match.Morphemes || ''}\t${morphCount}\t${morphAnalysis.structure}\t${morphAnalysis.analysis}\t${complexityLevel}` :
          `${result.word}\t${result.frequency}\t${match.Lemma || ''}\t${match.POS || ''}\t${match.Gloss || ''}\t${match.Morphemes || ''}\t${morphCount}`;
      });
    }).join('\n');
    navigator.clipboard.writeText(headers + rows)
      .then(() => { setError('Results copied to clipboard successfully!'); setTimeout(() => setError(''), 3000); })
      .catch(() => { setError('Failed to copy results.'); setTimeout(() => setError(''), 3000); });
  };

  useEffect(() => {
    if (csvData.length > 0 && !processingBatch) {
      processBatch();
    }
  }, [currentBatch, csvData]);

  const hasMoreBatches = csvData.length > 0 && currentBatch * batchSize < csvData.length;
  const processedCount = Math.min(currentBatch * batchSize, csvData.length);
  const chartData = Object.entries(matchStats.byPOS).map(([pos, count]) => ({ pos, count, percentage: matchStats.matched > 0 ? Math.round((count / matchStats.matched) * 100) : 0 })).sort((a, b) => b.count - a.count);

  return (
    <div className="p-4 max-w-7xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Georgian Morphological Analyzer</h1>
      {error && (
        <div className={`border px-4 py-3 rounded mb-4 ${error.includes('✓') ? 'bg-green-100 border-green-400 text-green-700' : 'bg-red-100 border-red-400 text-red-700'}`}>
          {error}
          <button className="float-right font-bold" onClick={() => setError('')}>×</button>
        </div>
      )}
      {loading ? (
        <div className="text-center py-8"><div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mb-2"></div><p>Processing data...</p></div>
      ) : (
        <div className="space-y-6">
          <div className="bg-white p-4 rounded shadow">
            <h2 className="text-xl font-semibold mb-3">Step 1: Load Reference Lexicon Data</h2>
            <div className="mb-3">
              <label className="block text-sm font-medium text-gray-700 mb-1">Paste your TSV data (must include 'Word' and 'Morphemes' headers):</label>
              <textarea
                className="w-full p-2 border rounded h-32 font-mono text-sm"
                value={referenceCSV}
                onChange={(e) => setReferenceCSV(e.target.value)}
                placeholder="Word[TAB]Frequency[TAB]Lemma[TAB]POS[TAB]Gloss[TAB]Morphemes
დაიფარე	3	ა/ იფარებს	VERB	covers	და-ი-ფარ-ე"
                disabled={referenceLoaded}
              />
            </div>
            <div className="flex gap-3 items-center">
              {!referenceLoaded ? (
                <button className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" onClick={loadReferenceData} disabled={!referenceCSV.trim() || loading}>Load Reference Data</button>
              ) : (<div className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm">✓ Reference data loaded ({referenceData.length} entries)</div>)}
              {referenceLoaded && (<button className="text-blue-600 hover:text-blue-800 text-sm" onClick={() => { setReferenceLoaded(false); setReferenceData([]); setIndexedData({}); }}>Reset Reference Data</button>)}
            </div>
          </div>
          <div className={`bg-white p-4 rounded shadow ${!referenceLoaded ? 'opacity-50' : ''}`}>
            <h2 className="text-xl font-semibold mb-3">Step 2: Input Your Georgian Word List</h2>
            <div className="mb-3">
              <label className="block text-sm font-medium text-gray-700 mb-1">Paste your CSV data with Georgian words:</label>
              <textarea
                className="w-full p-2 border rounded h-48 font-mono text-sm"
                value={userCSV}
                onChange={(e) => setUserCSV(e.target.value)}
                placeholder="Freq,Word
1,ორი
1,დაიფაროს
1,კარაქი"
                disabled={!referenceLoaded}
              />
            </div>
            <div className="flex flex-wrap gap-3 items-center">
              <button className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" onClick={parseUserCSV} disabled={!referenceLoaded || !userCSV.trim() || processingBatch}>Parse CSV</button>
              <div className="flex items-center"><label className="text-sm font-medium text-gray-700 mr-2">Batch Size:</label><select className="border rounded p-1" value={batchSize} onChange={(e) => setBatchSize(Number(e.target.value))} disabled={!referenceLoaded}><option value={100}>100</option><option value={250}>250</option><option value={500}>500</option><option value={1000}>1000</option></select></div>
              {csvData.length > 0 && <span className="text-sm text-gray-600">{processedCount} of {csvData.length} words processed ({Math.round((processedCount / csvData.length) * 100)}%)</span>}
              {csvData.length > 0 && <button className="text-red-600 hover:text-red-800 text-sm" onClick={resetResults}>Reset</button>}
            </div>
            {parseProgress > 0 && parseProgress < 100 && (<div className="w-full bg-gray-200 rounded-full h-2.5 mt-4"><div className="bg-blue-600 h-2.5 rounded-full" style={{ width: `${parseProgress}%` }}></div></div>)}
          </div>
          {matchedResults.length > 0 && (
            <div className="bg-white p-4 rounded shadow" ref={resultsRef}>
              <h2 className="text-xl font-semibold mb-3">Step 3: Matching Results</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                <div className="bg-gray-50 p-3 rounded">
                  <h3 className="font-semibold mb-2">Summary</h3>
                  <ul className="space-y-1">
                    <li>Total Words: <span className="font-medium">{matchStats.total}</span></li>
                    <li>Words Processed: <span className="font-medium">{processedCount}</span></li>
                    <li>Matched Words: <span className="font-medium text-green-600">{matchStats.matched}</span> ({processedCount > 0 ? Math.round((matchStats.matched / processedCount) * 100) : 0}%)</li>
                    <li>Unmatched Words: <span className="font-medium text-red-600">{matchStats.unmatched}</span> ({processedCount > 0 ? Math.round((matchStats.unmatched / processedCount) * 100) : 0}%)</li>
                  </ul>
                </div>
                {chartData.length > 0 && (<div className="bg-gray-50 p-3 rounded h-64"><h3 className="font-semibold mb-2">POS Distribution</h3><ResponsiveContainer width="100%" height="90%"><BarChart data={chartData.slice(0, 10)} margin={{ top: 5, right: 20, left: 10, bottom: 5 }}><CartesianGrid strokeDasharray="3 3" /><XAxis dataKey="pos" /><YAxis /><Tooltip formatter={(value, name, props) => [`${value} (${props.payload.percentage}%)`, 'Count']} /><Bar dataKey="count" fill="#3b82f6" /></BarChart></ResponsiveContainer></div>)}
              </div>
              <div className="flex flex-wrap gap-3 mb-4">
                {hasMoreBatches && (<button className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" onClick={processNextBatch} disabled={processingBatch}>{processingBatch ? 'Processing...' : `Process Next Batch (${Math.min(batchSize, csvData.length - processedCount)} words)`}</button>)}
                <button className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" onClick={copyResultsToClipboard} disabled={matchedResults.length === 0}>Copy Results to Clipboard</button>
                <div className="flex items-center"><input type="checkbox" id="showMorphAnalysis" checked={showMorphAnalysis} onChange={(e) => setShowMorphAnalysis(e.target.checked)} className="mr-2" /><label htmlFor="showMorphAnalysis" className="text-sm">Show morphological analysis</label></div>
                <div className="flex items-center ml-auto"><input type="checkbox" id="showUnmatched" checked={showUnmatched} onChange={(e) => setShowUnmatched(e.target.checked)} className="mr-2" /><label htmlFor="showUnmatched" className="text-sm">Show unmatched words</label></div>
              </div>
              {morphologicalStats && (
                <div className="mb-6 bg-gray-50 p-4 rounded"><h3 className="font-semibold mb-3">Morphological Analysis Summary</h3><div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4"><div><h4 className="font-medium mb-2">Complexity Distribution</h4><div className="space-y-1 text-sm">{Object.entries(morphologicalStats.complexityDistribution).map(([level, count]) => (<div key={level} className="flex justify-between"><span>{level}:</span><span className="font-medium">{count}</span></div>))}</div></div><div><h4 className="font-medium mb-2">Avg Complexity by POS</h4><div className="space-y-1 text-sm">{Object.entries(morphologicalStats.averageComplexityByPOS).sort(([,a],[,b]) => b-a).map(([pos, avg]) => (<div key={pos} className="flex justify-between"><span>{pos}:</span><span className="font-medium">{avg}</span></div>))}</div></div><div><h4 className="font-medium mb-2">Common Patterns</h4><div className="space-y-1 text-sm">{Object.entries(morphologicalStats.patterns).sort(([, a], [, b]) => b - a).slice(0, 5).map(([pattern, count]) => (<div key={pattern} className="flex justify-between"><span className="font-mono text-xs">{pattern}:</span><span className="font-medium">{count}</span></div>))}</div></div></div></div>
              )}
              <div className="overflow-x-auto"><table className="min-w-full border-collapse border border-gray-300"><thead><tr className="bg-gray-100"><th className="border border-gray-300 px-4 py-2 text-left">Word</th><th className="border border-gray-300 px-4 py-2 text-left">Freq</th><th className="border border-gray-300 px-4 py-2 text-left">Lemma</th><th className="border border-gray-300 px-4 py-2 text-left">POS</th><th className="border border-gray-300 px-4 py-2 text-left">Gloss</th><th className="border border-gray-300 px-4 py-2 text-left">Morphemes</th><th className="border border-gray-300 px-4 py-2 text-left">#</th>{showMorphAnalysis && (<><th className="border border-gray-300 px-4 py-2 text-left">Structure</th><th className="border border-gray-300 px-4 py-2 text-left">Analysis</th><th className="border border-gray-300 px-4 py-2 text-left">Complexity</th></>)}</tr></thead><tbody>
                {matchedResults.map((result) => {
                  if (result.matches.length === 0) {
                    if (!showUnmatched) return null;
                    return (<tr key={`unmatched-${result.originalIndex}`} className="bg-red-50"><td className="border border-gray-300 px-4 py-2">{result.word}</td><td className="border border-gray-300 px-4 py-2">{result.frequency}</td><td className="border border-gray-300 px-4 py-2 text-red-500" colSpan={showMorphAnalysis ? 8 : 5}>Not found</td></tr>);
                  }
                  return result.matches.map((match, matchIdx) => {
                    const morphCount = countMorphemes(match.Morphemes);
                    const morphAnalysis = analyzeMorphemeStructure(match.Morphemes, match.POS);
                    const complexityLevel = calculateComplexityLevel(morphCount);
                    return (
                      <tr key={`match-${result.originalIndex}-${matchIdx}`} className={matchIdx > 0 ? "bg-blue-50" : ""}>
                        <td className="border border-gray-300 px-4 py-2">{matchIdx === 0 ? result.word : ""}</td>
                        <td className="border border-gray-300 px-4 py-2">{matchIdx === 0 ? result.frequency : ""}</td>
                        <td className="border border-gray-300 px-4 py-2">{match.Lemma}</td><td className="border border-gray-300 px-4 py-2">{match.POS}</td>
                        <td className="border border-gray-300 px-4 py-2 text-sm">{match.Gloss}</td>
                        <td className="border border-gray-300 px-4 py-2 font-mono text-sm">{match.Morphemes}</td>
                        <td className="border border-gray-300 px-4 py-2 text-center font-medium">{morphCount}</td>
                        {showMorphAnalysis && (<>
                          <td className="border border-gray-300 px-4 py-2 font-mono text-xs">{morphAnalysis.structure}</td>
                          <td className="border border-gray-300 px-4 py-2 text-xs">{morphAnalysis.analysis}</td>
                          <td className="border border-gray-300 px-4 py-2 text-center text-sm">
                            <span className={`px-2 py-1 rounded text-xs ${
                              complexityLevel.startsWith('Very High') ? 'bg-red-100 text-red-800' :
                              complexityLevel.startsWith('High') ? 'bg-orange-100 text-orange-800' :
                              complexityLevel.startsWith('Moderate') ? 'bg-yellow-100 text-yellow-800' :
                              complexityLevel.startsWith('Low') ? 'bg-green-100 text-green-800' :
                              complexityLevel.startsWith('Simple') ? 'bg-blue-100 text-blue-800' : 'bg-gray-100 text-gray-800'
                            }`}>{complexityLevel}</span>
                          </td>
                        </>)}
                      </tr>
                    );
                  });
                })}
              </tbody></table></div>
              {hasMoreBatches && (<div className="mt-4 text-center"><button className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" onClick={processNextBatch} disabled={processingBatch}>{processingBatch ? 'Processing...' : `Process Next Batch (${Math.min(batchSize, csvData.length - processedCount)} words)`}</button></div>)}
            </div>
          )}
          <div className="bg-blue-50 p-4 rounded">
            <h3 className="font-semibold mb-2">Instructions</h3>
            <ol className="list-decimal pl-5 space-y-1">
              <li><strong>Step 1:</strong> Paste your lemmatized reference lexicon (TSV format, with headers 'Word', 'Morphemes', etc.) and click "Load Reference Data"</li>
              <li><strong>Step 2:</strong> Paste your Georgian word list CSV and click "Parse CSV"</li>
              <li><strong>Step 3:</strong> Process words in batches and review matches and the refined morphological analysis.</li>
            </ol>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
